INSERT INTO product ( id, title, avaliacao, numaval, price, category_id, shortdescription, fulldescription, image) VALUES ( null, 'Soin Kérastase', 4.5, 245, 141.00, 1, 'Condicionador Kérastase Elixir Ultime Le Fondant.', 'Condicionador para tratamento dos fios composto por óleo de camélia, proporcionando um brilho radiante e duradouro. Combinação única de 5 óleos preciosos. Óleo de Marula: Enriquecido com vitaminas C e D, proporciona uma nutrição intensa, proteção antioxidante para proporcionar aos seus cabelos os nutrientes essenciais. Óleo de Camélia: Ajuda a suavizar a fibra capilar com um resultado de brilho incrível. Óleo de Gérmen de Milho: Um ingrediente ativo que tem função importante para nutrição e proteção. Óleo de Pracaxi: Fornece brilho, nutrição e maciez aos fios. Óleo de Argan: Ajuda a restaurar a força dos fios e protege das agressões externas.', 'https://amarecuidar.s3.sa-east-1.amazonaws.com/hair_care/condicionador-kerastase.jpg');
INSERT INTO product ( id, title, avaliacao, numaval, price, category_id, shortdescription, fulldescription, image) VALUES ( null, 'Bain Kerastase', 4.0, 120, 99.00, 1, 'Shampoo Kérastase Elixir Ultime Le Bain.', 'Shampoo de limpeza composto por óleo de camélia e óleo de Marula que remove impurezas e purifica o cabelo, proporcionando um brilho radiante e duradouro. Combinação única de 5 óleos preciosos. Óleo de Marula: Enriquecido com vitaminas C e D, proporciona uma nutrição intensa, proteção antioxidante para proporcionar aos seus cabelos os nutrientes essenciais. Óleo de Camélia: Ajuda a suavizar a fibra capilar com um resultado de brilho incrível. Óleo de Gérmen de Milho: Um ingrediente ativo que tem função importante para nutrição e proteção. Óleo de Pracaxi: Fornece brilho, nutrição e maciez aos fios. Óleo de Argan: Ajuda a restaurar a força dos fios e protege das agressões externas.', 'https://amarecuidar.s3.sa-east-1.amazonaws.com/hair_care/shampoo-elixir.jpg');
INSERT INTO product ( id, title, avaliacao, numaval, price, category_id, shortdescription, fulldescription, image) VALUES ( null, 'Masque Kérastase', 5.0, 35, 249.00, 1, 'Máscara de Tratamento Kérastase Elixir Ultime Le Masque.', 'Máscara para tratamento profundo dos fios composta por óleo de camélia e óleo de Marula, proporcionando um brilho radiante e duradouro. Combinação única de 5 óleos preciosos. Óleo de Marula: Enriquecido com vitaminas C e D, proporciona uma nutrição intensa, proteção antioxidante para proporcionar aos seus cabelos os nutrientes essenciais. Óleo de Camélia: Ajuda a suavizar a fibra capilar com um resultado de brilho incrível. Óleo de Gérmen de Milho: Um ingrediente ativo que tem função importante para nutrição e proteção. Óleo de Pracaxi: Fornece brilho, nutrição e maciez aos fios. Óleo de Argan: Ajuda a restaurar a força dos fios e protege das agressões externas.', 'https://amarecuidar.s3.sa-east-1.amazonaws.com/hair_care/creme-elixir.jpg');
INSERT INTO product ( id, title, avaliacao, numaval, price, category_id, shortdescription, fulldescription, image) VALUES ( null, 'Gel Modelador Keune', 4.8, 210, 98.00, 1, 'Gel Modelador Keune 1922 Classic Gel.', 'Gel modelador de alta fixação, 1922 Classic Gel é indicado para cabelos médios e curtos e penteados que necessitem de alta fixação e brilho. Sua fórmula contém creatina e óleo de rícino, um poderoso fortificante natural com propriedades hidratantes. Deixa qualquer penteado no lugar, com toque extra de brilho. Adequado para cabelos médios e curtos. Penteados que necessitem de alta fixação e brilho. ', 'https://amarecuidar.s3.sa-east-1.amazonaws.com/hair_care/gel-keune.jpg');
INSERT INTO product ( id, title, avaliacao, numaval, price, category_id, shortdescription, fulldescription, image) VALUES ( null, 'Shampoo Keune', 4.2, 463, 34.50, 1, 'Shampoo Neutro Keune 1922 Refreshing Shampoo.', 'O 1922 Refreshing Shampoo é um shampoo neutro de uso diário recomendado para todos os tipos de cabelo que proporciona uma agradável sensação de frescor e é ideal para quem mora em regiões quentes ou pratica esportes. O frescor desse shampoo vem da fórmula, rica em extrato de hortelã, pimenta e mentol. Adequado para higienizar qualquer tipo de cabelo, promovendo uma agradável sensação de frescor. Suas propriedades mentoladas proporcionam ao couro cabeludo uma sensação de limpeza e frescor, estimulando a circulação sanguínea.', 'https://amarecuidar.s3.sa-east-1.amazonaws.com/hair_care/shampoo-keune.jpg');
INSERT INTO product ( id, title, avaliacao, numaval, price, category_id, shortdescription, fulldescription, image) VALUES ( null, 'Shampoo Redken', 4.6, 310, 99.00, 1, 'Redken Brews 3-In-1 - Shampoo Multifuncional.', 'O Shampoo Redken Brews 3 em 1 promove limpeza e deixa seus fios de seus cabelos macios e fáceis de pentear. É 3 em 1 pelo fato de poder ser aplicado tanto nos cabelos, na barba e no corpo. Resultado esperado: Pele e cabelos hidratados e relaxados. Indicado: Para todos os tipos de cabelo, barba e pele. Tecnologia: Possui uma fórmula antirressecamento à base de malte. Tem ação relaxante, promove hidratação e limpeza e tratamento profissional para os seus cabelos.', 'https://amarecuidar.s3.sa-east-1.amazonaws.com/hair_care/shampoo-redken.jpg');
