drop table if exists category CASCADE;
drop table if exists product CASCADE;
create table category (id bigint generated by default as identity, name varchar(255), primary key (id));
create table product (id bigint generated by default as identity, shortdescription varchar(255), fulldescription text, image varchar(255), price double, avaliacao double, numaval int, title varchar(255), category_id bigint, primary key (id));
alter table product add constraint Ecomerce foreign key (category_id) references category;

